if(!$(this).is('.input')){    
    $(this).addClass('input').html('<input type="text" value="'+ $(this).text() +'" />').find('input').focus().blur(function(){ 
        if (source_text == $(this).val()){   
            $(this).parent().removeClass('input').html($(this).val() || 0);    
        }
